import React, { useState } from 'react';
import { 
  Typography, Card, CardContent, CardMedia, Box, Container, Tabs, Tab, 
  styled, Avatar, Chip, Button, IconButton, Alert, TextField, Grid, Paper,
  FormControl, InputLabel, Select, MenuItem, Rating, Stack
} from '@mui/material';
import StarIcon from '@mui/icons-material/Star';
import KeyboardArrowRightIcon from '@mui/icons-material/KeyboardArrowRight';
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';
import {
  Email as EmailIcon,
  FormatQuote as FormatQuoteIcon,
  TipsAndUpdates as TipsAndUpdatesIcon
} from '@mui/icons-material';

// Enhanced Card with modern styling and hover effects
const EnhancedCard = styled(Card)(({ theme }) => ({
  width: '300px',
  height: '380px',
  borderRadius: '16px',
  overflow: 'hidden',
  boxShadow: '0 10px 30px rgba(0, 0, 0, 0.08)',
  transition: 'all 0.3s cubic-bezier(0.25, 0.8, 0.25, 1)',
  background: '#fff',
  position: 'relative',
  '&:hover': {
    transform: 'translateY(-8px)',
    boxShadow: '0 15px 40px rgba(0, 0, 0, 0.12)',
  },
}));

const CardMediaWrapper = styled(Box)(({ theme }) => ({
  position: 'relative',
  height: '200px',
  overflow: 'hidden',
}));

const DoctorInfo = styled(CardContent)(({ theme }) => ({
  padding: theme.spacing(2),
  position: 'relative',
}));

const DoctorName = styled(Typography)(({ theme }) => ({
  fontWeight: 700,
  fontSize: '1.2rem',
  marginBottom: theme.spacing(1),
  color: '#03265a',
}));

const DoctorDetails = styled(Typography)(({ theme }) => ({
  fontSize: '0.9rem',
  color: theme.palette.text.secondary,
  marginBottom: theme.spacing(1),
  display: '-webkit-box',
  WebkitLineClamp: 3,
  WebkitBoxOrient: 'vertical',
  overflow: 'hidden',
  textOverflow: 'ellipsis',
}));

const StyledTabs = styled(Tabs)(({ theme }) => ({
  marginBottom: theme.spacing(4),
  '& .MuiTabs-indicator': {
    backgroundColor: '#03265a',
  },
  '& .MuiTab-root': {
    textTransform: 'none',
    fontWeight: 600,
    fontSize: '1rem',
    color: theme.palette.text.secondary,
    '&.Mui-selected': {
      color: '#03265a',
    },
  },
}));

const SpecialtyHeading = styled(Typography)(({ theme }) => ({
  fontWeight: 800,
  fontSize: '2.2rem',
  marginBottom: theme.spacing(2),
  textAlign: 'center',
  background: 'linear-gradient(45deg, #03265a 30%, #2752af 90%)',
  WebkitBackgroundClip: 'text',
  WebkitTextFillColor: 'transparent',
}));

const ScrollControls = styled(Box)(({ theme }) => ({
  display: 'flex',
  justifyContent: 'center',
  marginTop: theme.spacing(3),
  gap: theme.spacing(2),
}));

const ScrollButton = styled(IconButton)(({ theme }) => ({
  backgroundColor: '#03265a',
  color: '#fff',
  '&:hover': {
    backgroundColor: '#2752af',
  },
}));

const BookButton = styled(Button)(({ theme }) => ({
  position: 'absolute',
  bottom: theme.spacing(2),
  right: theme.spacing(2),
  borderRadius: '20px',
  textTransform: 'none',
  fontWeight: 600,
  fontSize: '0.8rem',
  padding: '4px 12px',
  backgroundColor: '#03265a',
  '&:hover': {
    backgroundColor: '#2752af',
  },
}));

const RatingChip = styled(Chip)(({ theme }) => ({
  position: 'absolute',
  top: 8,
  right: 8,
  backgroundColor: 'rgba(255, 255, 255, 0.9)',
  color: '#f57c00',
  fontWeight: 600,
  '& .MuiChip-icon': {
    color: '#f57c00',
  },
}));

const DoctorAvatar = styled(Avatar)(({ theme }) => ({
  width: 64,
  height: 64,
  border: '3px solid #fff',
  position: 'absolute',
  bottom: '-32px',
  left: '20px',
  zIndex: 1,
  boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
}));

const NewsCard = styled(Card)(({ theme }) => ({
  height: '100%',
  transition: 'transform 0.3s ease',
  '&:hover': {
    transform: 'translateY(-8px)',
  },
}));

const TestimonialCard = styled(Card)(({ theme }) => ({
  padding: theme.spacing(3),
  height: '100%',
  transition: 'transform 0.3s ease',
  '&:hover': {
    transform: 'translateY(-8px)',
  },
}));

const SectionTitle = styled(Typography)(({ theme }) => ({
  fontWeight: 700,
  fontSize: '2rem',
  marginBottom: theme.spacing(4),
  textAlign: 'center',
  color: '#03265a',
}));

const SpecialtyDoctorsSlideshow = () => {
  // Specialty data (same as your original)
  const specialties = [
    {
