import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { 
    Button, 
    TextField, 
    Card, 
    CardContent, 
    Box, 
    Typography,
    Container,
    InputAdornment,
    IconButton
} from '@mui/material';
import { makeStyles } from '@mui/styles';
import { setAuthToken } from '../../utils/auth';
import { buildApiUrl } from '../../config/api';
import { 
    Person as PersonIcon,
    Lock as LockIcon,
    Visibility as VisibilityIcon,
    VisibilityOff as VisibilityOffIcon 
} from '@mui/icons-material';

const useStyles = makeStyles({
    root: {
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        background: 'linear-gradient(135deg, #001f3f 0%, #1a365d 100%)',
        padding: '20px',
    },
    card: {
        backgroundColor: 'rgba(255, 255, 255, 0.95)',
        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.2)',
        borderRadius: '16px',
        maxWidth: '450px',
        width: '100%',
        padding: '40px 30px',
        textAlign: 'center',
        transition: 'transform 0.3s ease-in-out',
        '&:hover': {
            transform: 'translateY(-5px)',
        },
    },
    form: {
        display: 'flex',
        flexDirection: 'column',
        gap: '24px',
        marginTop: '32px',
    },
    textField: {
        '& .MuiOutlinedInput-root': {
            borderRadius: '12px',
            '&:hover fieldset': {
                borderColor: '#1976d2',
            },
        },
    },
    button: {
        marginTop: '16px',
        padding: '12px',
        borderRadius: '12px',
        fontSize: '16px',
        fontWeight: 600,
        textTransform: 'none',
        backgroundColor: '#1976d2',
        '&:hover': {
            backgroundColor: '#1565c0',
        },
    },
    signupButton: {
        marginTop: '12px',
        borderRadius: '12px',
        textTransform: 'none',
    },
    welcomeText: {
        color: '#1976d2',
        marginBottom: '8px',
        fontWeight: 700,
        fontSize: '2rem',
    },
    subtitle: {
        color: '#666',
        marginBottom: '24px',
    },
});

const Login = ({ selectedOption }) => {
    const classes = useStyles();
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [showPassword, setShowPassword] = useState(false);
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(buildApiUrl('/api/login'), formData);

            // Check if we have a valid response with token
            if (!response.data || !response.data.token) {
                throw new Error('Invalid response from server - no token received');
            }

            // Set token in localStorage and auth headers
            const { token, user } = response.data;
            localStorage.setItem('token', token);
            setAuthToken(token);  // Set token in axios headers

            // Set user role
            const userRole = user.role;
            localStorage.setItem('userRole', userRole);

            const roleToRoute = {
                'Admin': 'AdminProfile',
                'Doctor': 'DoctorProfile',
                'Patient': 'ClientProfile',
                'Client': 'ClientProfile'
            };

            const redirectPath = `/${roleToRoute[userRole]}`;
            if (!redirectPath) {
                throw new Error('Invalid user role');
            }

            // Navigate with user data
            navigate(redirectPath, { 
                state: { 
                    user,
                    isAuthenticated: true
                } 
            });

        } catch (error) {
            console.error('Login error:', error);
            const errorMessage = error.response?.data?.message || error.message || 'Login failed. Please check your credentials.';
            alert(errorMessage);
        }
    };

    const handleSignup = () => {
        let optval = "/" + selectedOption + "Signup";
        navigate(optval, { replace: true });
    };

    return (
        <Box className={classes.root}>
            <Container maxWidth="sm">
                <Card className={classes.card}>
                    <CardContent>
                        <Typography variant="h4" className={classes.welcomeText}>
                            Welcome Back
                        </Typography>
                        <Typography variant="body1" className={classes.subtitle}>
                            Please sign in to continue
                        </Typography>

                        <form className={classes.form} onSubmit={handleLogin}>
                            <TextField
                                className={classes.textField}
                                fullWidth
                                id="email"
                                label="Email"
                                type="email"
                                variant="outlined"
                                value={formData.email}
                                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                required
                                InputProps={{
                                    startAdornment: (
                                        <InputAdornment position="start">
                                            <PersonIcon color="action" />
                                        </InputAdornment>
                                    ),
                                }}
                            />

                            <TextField
                                className={classes.textField}
                                fullWidth
                                id="password"
                                label="Password"
                                type={showPassword ? 'text' : 'password'}
                                variant="outlined"
                                value={formData.password}
                                onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                                required
                                InputProps={{
                                    startAdornment: (
                                        <InputAdornment position="start">
                                            <LockIcon color="action" />
                                        </InputAdornment>
                                    ),
                                    endAdornment: (
                                        <InputAdornment position="end">
                                            <IconButton
                                                onClick={() => setShowPassword(!showPassword)}
                                                edge="end"
                                            >
                                                {showPassword ? <VisibilityOffIcon /> : <VisibilityIcon />}
                                            </IconButton>
                                        </InputAdornment>
                                    ),
                                }}
                            />

                            <Button
                                className={classes.button}
                                fullWidth
                                variant="contained"
                                type="submit"
                            >
                                Sign In
                            </Button>

                            <Button
                                className={classes.signupButton}
                                fullWidth
                                variant="outlined"
                                onClick={handleSignup}
                            >
                                Create New Account
                            </Button>
                        </form>
                    </CardContent>
                </Card>
            </Container>
        </Box>
    );
};

export default Login;
