import React, { useState } from 'react';
import axios from 'axios';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import { makeStyles } from '@mui/styles';
import { Link, useNavigate } from 'react-router-dom';
import Alert from '@mui/material/Alert';
import Snackbar from '@mui/material/Snackbar';
import { buildApiUrl } from '../../config/api';

const useStyles = makeStyles({
  root: {
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#001f3f',
    paddingLeft: '10px',
    paddingRight: '10px',
  },
  card: {
    backgroundColor: '#f5f5f5',
    marginTop: '35px',
    boxShadow: '0px 0px 20px rgba(0, 0, 0, 0.1)',
    borderRadius: '10px',
    maxWidth: '400px',
    padding: '20px',
    textAlign: 'center',
    transition: 'transform 0.3s ease-in-out',
    '&:hover': {
      transform: 'scale(1.05)',
    },
  },
  title: {
    fontSize: '35px',
    color: '#1976d2',
    marginBottom: '20px',
  },
  input: {
    marginBottom: '15px',
  },
  button: {
    marginTop: '15px',
  },
  connectionInfo: {
    marginTop: '20px',
    padding: '10px',
    backgroundColor: '#e3f2fd',
    borderRadius: '5px',
    fontSize: '14px',
    color: '#0d47a1',
  }
});

const DoctorSignup = () => {
  const navigate = useNavigate();
  const classes = useStyles();
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [openSnackbar, setOpenSnackbar] = useState(false);

  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    licenseNumber: '',
    specializationName: '',
    affiliation: '',
    contactNumber: '',
    termsAndConditions: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleCloseSnackbar = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setOpenSnackbar(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Doctor Signup Data:', formData);

    // Input validation conditions
    if (formData.password !== formData.confirmPassword) {
      setErrorMessage('Passwords do not match!');
      setOpenSnackbar(true);
      return;
    }

    // Add more validation conditions as needed...

    try {
      const response = await axios.post(buildApiUrl('/api/signup/doctor'), formData);
      console.log('Signup successful:', response.data);

      setFormData({
        fullName: '',
        email: '',
        password: '',
        confirmPassword: '',
        licenseNumber: '',
        specializationName: '',
        affiliation: '',
        contactNumber: '',
        termsAndConditions: false,
      });

      // Show success message
      setSuccessMessage('Registration successful! Redirecting to login...');
      setOpenSnackbar(true);

      // Redirect after showing the message
      setTimeout(() => {
        navigate('/login');
      }, 2000);
    } catch (error) {
      console.error('Signup error:', error.response ? error.response.data : error.message);
      setErrorMessage(error.response?.data?.message || 'Signup failed. Please try again.');
      setOpenSnackbar(true);
    }
  };

  return (
    <div className={classes.root}>
      <Card className={classes.card}>
        <CardContent>
          <Typography variant="h1" className={classes.title}>Doctor Signup</Typography>

          <Typography variant="body2" className={classes.connectionInfo}>
            MongoDB Connection: mongodb+srv://i222379:&lt;db_password&gt;@cluster0.rjuarp7.mongodb.net/
          </Typography>

          <TextField
            type="text"
            name="fullName"
            label="Full Name"
            value={formData.fullName}
            onChange={handleChange}
            required
            className={classes.input}
          />

          <TextField
            type="email"
            name="email"
            label="Email"
            value={formData.email}
            onChange={handleChange}
            required
            className={classes.input}
          />

          <TextField
            type="password"
            name="password"
            label="Password"
            value={formData.password}
            onChange={handleChange}
            required
            className={classes.input}
          />

          <TextField
            type="password"
            name="confirmPassword"
            label="Confirm Password"
            value={formData.confirmPassword}
            onChange={handleChange}
            required
            className={classes.input}
          />

          <TextField
            type="text"
            name="licenseNumber"
            label="Medical License Number"
            value={formData.licenseNumber}
            onChange={handleChange}
            required
            className={classes.input}
          />

          <TextField
            type="text"
            name="specializationName"
            label="Specialization"
            value={formData.specializationName}
            onChange={handleChange}
            required
            className={classes.input}
          />

          <TextField
            type="text"
            name="affiliation"
            label="Hospital/Clinic Affiliation"
            value={formData.affiliation}
            onChange={handleChange}
            className={classes.input}
          />

          <TextField
            type="text"
            name="contactNumber"
            label="Contact Number"
            value={formData.contactNumber}
            onChange={handleChange}
            required
            className={classes.input}
          />

          <div>
            <label>
              <input
                type="checkbox"
                name="termsAndConditions"
                checked={formData.termsAndConditions}
                onChange={handleChange}
                required
              />
              Agree to Terms and Conditions
            </label>
          </div>

          <Button 
            type="submit" 
            variant="contained" 
            className={classes.button} 
            onClick={handleSubmit}
            fullWidth
          >
            Signup
          </Button>
        </CardContent>
      </Card>

      <Snackbar 
        open={openSnackbar} 
        autoHideDuration={6000} 
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert 
          onClose={handleCloseSnackbar} 
          severity={successMessage ? "success" : "error"} 
          sx={{ width: '100%' }}
        >
          {successMessage || errorMessage}
        </Alert>
      </Snackbar>
    </div>
  );
};

export default DoctorSignup;
