import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import AiChatbot from './Client/AiChatbot';
import Appointments from './Client/Appointments';
import AppointmentView from './Client/AppointmentView';
import MedicalRecords from './Client/MedicalRecords';
import HealthArticle from './Client/HealthActivity';
// import Timer from '../auth/Timer';
import Stats from './Client/stats';
import { 
  Typography, 
  Box, 
  AppBar, 
  Toolbar, 
  IconButton, 
  Button, 
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  ListItemButton,
  Paper,
  Avatar,
  Divider
} from '@mui/material';
import {
  Menu as MenuIcon,
  Person as ProfileIcon,
  CalendarMonth as AppointmentsIcon,
  Visibility as ViewIcon,
  DescriptionOutlined as RecordsIcon,
  SmartToy as AssistantIcon,
  Logout as LogoutIcon
} from '@mui/icons-material';
import { ThemeProvider, createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#2563eb',
    },
  },
});

const ClientProfile = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const [userData, setUserData] = useState(null);
  const [activeSection, setActiveSection] = useState('profile');
  const [drawerOpen, setDrawerOpen] = useState(false);

  useEffect(() => {
    if (location.state?.user) {
      setUserData(location.state.user);
    } else {
      // If no user data, redirect to login
      navigate('/login');
    }
  }, [location.state, navigate]);

  const navigateToSection = (section) => {
    setActiveSection(section);
    setDrawerOpen(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const menuItems = [
    { text: 'Profile', icon: <ProfileIcon />, section: 'profile' },
    { text: 'Appointments', icon: <AppointmentsIcon />, section: 'appointments' },
    { text: 'View Appointments', icon: <ViewIcon />, section: 'view-appointments' },
    { text: 'Medical Records', icon: <RecordsIcon />, section: 'medical-records' },
    { text: 'Medical Assistant', icon: <AssistantIcon />, section: 'medical-assistant' },
  ];

  const renderContent = () => {
    if (!userData) {
      return <Typography>Loading...</Typography>;
    }

    switch (activeSection) {
      case 'appointments':
        return <Appointments userId={userData._id} />;
      case 'view-appointments':
        return <AppointmentView userId={userData._id} />;
      case 'medical-assistant':
        return <AiChatbot />;
      case 'medical-records':
        return <MedicalRecords userId={userData._id} />;
      default:
        return (
          <Box sx={{ mt: 4 }}>
            <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
              <Typography variant="h4" gutterBottom>Welcome, {userData.fullName}</Typography>
              <Typography variant="body1" gutterBottom>Email: {userData.email}</Typography>
              <Typography variant="body1" gutterBottom>Contact: {userData.contactNumber}</Typography>
            </Paper>
            <Stats userId={userData._id} />
          </Box>
        );
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ display: 'flex', minHeight: '100vh' }}>
        <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
          <Toolbar>
            <IconButton
              size="large"
              edge="start"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 2, display: { sm: 'none' } }}
              onClick={() => setDrawerOpen(true)}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              {userData ? userData.fullName : 'User Profile'}
            </Typography>
            <Button color="inherit" onClick={handleLogout} startIcon={<LogoutIcon />}>
              Logout
            </Button>
          </Toolbar>
        </AppBar>

        <Drawer
          variant="permanent"
          sx={{
            display: { xs: 'none', sm: 'block' },
            width: 280,
            flexShrink: 0,
            '& .MuiDrawer-paper': {
              width: 280,
              boxSizing: 'border-box',
              backgroundColor: '#f8fafc',
              borderRight: '1px solid #e2e8f0',
            },
          }}
        >
          <Toolbar />
          <Box sx={{ overflow: 'auto', mt: 2 }}>
            {userData && (
              <Box sx={{ px: 3, pb: 3 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                  <Avatar
                    sx={{
                      width: 64,
                      height: 64,
                      bgcolor: theme.palette.primary.main,
                      fontSize: '1.5rem',
                    }}
                  >
                    {userData.fullName?.charAt(0)}
                  </Avatar>
                  <Box>
                    <Typography variant="subtitle1" fontWeight="bold">
                      {userData.fullName}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Patient
                    </Typography>
                  </Box>
                </Box>
              </Box>
            )}
            <Divider sx={{ mb: 2 }} />
            <List>
              {menuItems.map((item) => (
                <ListItem key={item.text} disablePadding>
                  <ListItemButton
                    onClick={() => navigateToSection(item.section)}
                    selected={activeSection === item.section}
                    sx={{
                      mx: 1,
                      borderRadius: '8px',
                      mb: 1,
                      '&.Mui-selected': {
                        backgroundColor: 'primary.main',
                        color: 'white',
                        '&:hover': {
                          backgroundColor: 'primary.dark',
                        },
                        '& .MuiListItemIcon-root': {
                          color: 'white',
                        },
                      },
                      '&:hover': {
                        backgroundColor: 'rgba(37, 99, 235, 0.08)',
                      },
                    }}
                  >
                    <ListItemIcon sx={{ 
                      minWidth: 40,
                      color: activeSection === item.section ? 'white' : 'primary.main'
                    }}>
                      {item.icon}
                    </ListItemIcon>
                    <ListItemText 
                      primary={item.text}
                      primaryTypographyProps={{
                        fontSize: '0.95rem',
                        fontWeight: activeSection === item.section ? 600 : 400
                      }}
                    />
                  </ListItemButton>
                </ListItem>
              ))}
            </List>
          </Box>
        </Drawer>

        <Drawer
          variant="temporary"
          anchor="left"
          open={drawerOpen}
          onClose={() => setDrawerOpen(false)}
          sx={{
            display: { xs: 'block', sm: 'none' },
            '& .MuiDrawer-paper': {
              width: 280,
              backgroundColor: '#f8fafc',
            },
          }}
        >
          <Toolbar />
          <Box sx={{ overflow: 'auto', mt: 2 }}>
            {userData && (
              <Box sx={{ px: 3, pb: 3 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                  <Avatar
                    sx={{
                      width: 64,
                      height: 64,
                      bgcolor: theme.palette.primary.main,
                      fontSize: '1.5rem',
                    }}
                  >
                    {userData.fullName?.charAt(0)}
                  </Avatar>
                  <Box>
                    <Typography variant="subtitle1" fontWeight="bold">
                      {userData.fullName}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Patient
                    </Typography>
                  </Box>
                </Box>
              </Box>
            )}
            <Divider sx={{ mb: 2 }} />
            <List>
              {menuItems.map((item) => (
                <ListItem key={item.text} disablePadding>
                  <ListItemButton
                    onClick={() => navigateToSection(item.section)}
                    selected={activeSection === item.section}
                    sx={{
                      mx: 1,
                      borderRadius: '8px',
                      mb: 1,
                      '&.Mui-selected': {
                        backgroundColor: 'primary.main',
                        color: 'white',
                        '&:hover': {
                          backgroundColor: 'primary.dark',
                        },
                        '& .MuiListItemIcon-root': {
                          color: 'white',
                        },
                      },
                      '&:hover': {
                        backgroundColor: 'rgba(37, 99, 235, 0.08)',
                      },
                    }}
                  >
                    <ListItemIcon sx={{ 
                      minWidth: 40,
                      color: activeSection === item.section ? 'white' : 'primary.main'
                    }}>
                      {item.icon}
                    </ListItemIcon>
                    <ListItemText 
                      primary={item.text}
                      primaryTypographyProps={{
                        fontSize: '0.95rem',
                        fontWeight: activeSection === item.section ? 600 : 400
                      }}
                    />
                  </ListItemButton>
                </ListItem>
              ))}
            </List>
          </Box>
        </Drawer>

        <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
          <Toolbar />
          {renderContent()}
          <Box sx={{ mt: 4 }}>
            <HealthArticle/>
          </Box>
        </Box>
      </Box>
    </ThemeProvider>
  );
};

export default ClientProfile;
