import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Box from '@mui/material/Box';
import { makeStyles } from '@mui/styles';
import Typography from '@mui/material/Typography';
import { setAuthToken } from '../../utils/auth';
import { buildApiUrl } from '../../config/api';

const useStyles = makeStyles({
    root: {
      minHeight: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: '#001f3f', // Navy blue background color
    },
    card: {
      backgroundColor: '#f5f5f5', // Light background color
      boxShadow: '0px 0px 20px rgba(0, 0, 0, 0.1)', // Box shadow for modern look
      borderRadius: '10px', // Rounded corners for modern look
      maxWidth: '400px', // Limit width for better readability on large screens
      padding: '20px', // Add padding for better spacing
      textAlign: 'center', // Center text content
      transition: 'transform 0.3s ease-in-out', // Smooth transition for hover effect
      '&:hover': {
        transform: 'scale(1.05)', // Scale up the card on hover
      },
    },
  });


const Login = ({ selectedOption }) => {
    const classes = useStyles();
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [error, setError] = useState('');
    const [debugInfo, setDebugInfo] = useState({});

    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        setDebugInfo({ formData, status: 'Submitting...' });
        try {
            const response = await axios.post(buildApiUrl('/api/login'), formData);
            setDebugInfo(prev => ({ ...prev, response: response.data, status: 'Success' }));

            if (!response.data.token) {
                throw new Error('No token received from server');
            }

            // Store token and user role
            localStorage.setItem('token', response.data.token);
            const userRole = response.data.user.role === 'Patient' ? 'client' : response.data.user.role.toLowerCase();
            localStorage.setItem('userRole', userRole);

            // Set the auth token for future requests
            setAuthToken(response.data.token);

            // Map roles to their corresponding profile routes
            const roleToRoute = {
                'Admin': 'AdminProfile',
                'Doctor': 'DoctorProfile',
                'Patient': 'ClientProfile'
            };

            const redirectPath = `/${roleToRoute[response.data.user.role]}`;
            if (!redirectPath) {
                throw new Error('Invalid user role');
            }

            // Navigate to the appropriate profile page
            navigate(redirectPath);

        } catch (error) {
            setDebugInfo(prev => ({
                ...prev,
                error: error.toString(),
                errorResponse: error.response ? error.response.data : undefined,
                status: 'Error'
            }));
            console.error('Login error:', error);
            setError(error.response?.data?.message || 'Login failed. Please check your credentials.');
        }
    };

    const handleSignup = () => {
        let optval = "/" + selectedOption + "Signup";
        console.log(optval);
        navigate(optval, { replace: true });
    };

    return (
        <div className={classes.root}>
            <Card className={classes.card}>
                <CardContent>
                    <Typography variant="h1" style={{ color: '#1976d2', marginBottom: '20px', fontSize:'35px' }}>
                        Login
                    </Typography>
                    {/* Debugging Box */}
                    <Box mb={2} p={2} style={{ background: '#fffbe6', border: '1px solid #ffe58f', borderRadius: 8, fontSize: 12, color: '#ad6800', marginBottom: 20, wordBreak: 'break-all' }}>
                        <b>Debug Info:</b>
                        <pre style={{ whiteSpace: 'pre-wrap', margin: 0 }}>{JSON.stringify(debugInfo, null, 2)}</pre>
                    </Box>
                    {error && (
                        <Typography color="error" style={{ marginBottom: '20px' }}>
                            {error}
                        </Typography>
                    )}
                    <form onSubmit={handleLogin}>
                        <div>
                            <TextField
                                id="email"
                                label="Email"
                                type="email"
                                value={formData.email}
                                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                required
                                fullWidth
                                margin="normal"
                                autoComplete="email"
                            />
                        </div>
                        <div>
                            <TextField
                                id="password"
                                label="Password"
                                type="password"
                                value={formData.password}
                                onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                                required
                                fullWidth
                                margin="normal"
                                autoComplete="current-password"
                            />
                        </div>
                        <Button 
                            type="submit" 
                            variant="contained" 
                            fullWidth
                            style={{marginTop:'20px'}}
                        >
                            Login
                        </Button>
                        <Button 
                            variant="outlined" 
                            onClick={handleSignup} 
                            fullWidth
                            style={{marginTop:'11px'}}
                        >
                            Sign Up
                        </Button>
                    </form>
                </CardContent>
            </Card>
        </div>
    );
};

export default Login;
