import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import AiChatbot from './Client/AiChatbot';
import Appointments from './Client/Appointments';
import AppointmentView from './Client/AppointmentView';
import MedicalRecords from './Client/MedicalRecords';
import HealthArticle from './Client/HealthActivity';
// import Timer from '../auth/Timer';
import Stats from './Client/stats';
import { 
  Typography, 
  Box, 
  AppBar, 
  Toolbar, 
  IconButton, 
  Button, 
  Drawer,
  List,
  ListItem,
  ListItemText,
  Paper,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { ThemeProvider, createTheme } from '@mui/material/styles';

const theme = createTheme();

const ClientProfile = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const [userData, setUserData] = useState(null);
  const [activeSection, setActiveSection] = useState('profile');
  const [drawerOpen, setDrawerOpen] = useState(false);

  useEffect(() => {
    console.log('Location state:', location.state);

    if (location.state?.user) {
      console.log('User data from location state:', location.state.user);
      setUserData(location.state.user);
    } else {
      console.log('No user data in location state, checking token...');
      // Try to get user data from JWT token
      const token = localStorage.getItem('token');
      if (token) {
        // If we have a token, we can either:
        // 1. Decode the token to get basic user info
        try {
          // Simple JWT decoding (not secure, just for display purposes)
          const base64Url = token.split('.')[1];
          const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
          const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
            return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
          }).join(''));

          const decodedToken = JSON.parse(jsonPayload);
          console.log('Decoded token:', decodedToken);

          // Set minimal user data from token
          setUserData({
            _id: decodedToken.userId,
            email: decodedToken.email,
            role: decodedToken.role
          });

          // 2. Make an API call to get full user data
          // This would be better in a production app
          // fetchUserData(decodedToken.userId);
        } catch (error) {
          console.error('Error decoding token:', error);
          navigate('/login');
        }
      } else {
        // If no user data and no token, redirect to login
        console.log('No token found, redirecting to login');
        navigate('/login');
      }
    }
  }, [location.state, navigate]);

  const navigateToSection = (section) => {
    setActiveSection(section);
    setDrawerOpen(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const renderContent = () => {
    if (!userData) {
      return <Typography>Loading...</Typography>;
    }

    switch (activeSection) {
      case 'appointments':
        return <Appointments userId={userData._id} />;
      case 'view-appointments':
        return <AppointmentView userId={userData._id} />;
      case 'medical-assistant':
        return <AiChatbot />;
      case 'medical-records':
        return <MedicalRecords userId={userData._id} />;
      default:
        return (
          <Box sx={{ mt: 4 }}>
            <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
              <Typography variant="h4" gutterBottom>Welcome, {userData.fullName}</Typography>
              <Typography variant="body1" gutterBottom>Email: {userData.email}</Typography>
              <Typography variant="body1" gutterBottom>Contact: {userData.contactNumber}</Typography>
            </Paper>
            <Stats userId={userData._id} />
          </Box>
        );
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="static">
          <Toolbar>
            <IconButton
              size="large"
              edge="start"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 2 }}
              onClick={() => setDrawerOpen(true)}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              {userData ? userData.fullName : 'User Profile'}
            </Typography>
            <Button color="inherit" onClick={handleLogout}>Logout</Button>
          </Toolbar>
        </AppBar>
      </Box>
      <Box>
        {/* <Timer/> */}
      </Box>
      <Drawer
        anchor="left"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        sx={{
          '& .MuiDrawer-paper': {
            width: 240,
            background: '#f0f2f5',
          },
        }}
      >
        <List>
          {['Profile', 'Appointments', 'View Appointments', 'Medical Records', 'Medical Assistant'].map((text) => (
            <ListItem 
              button 
              key={text} 
              onClick={() => navigateToSection(text.toLowerCase().replace(' ', '-'))}
              sx={{
                my: 1,
                mx: 1,
                borderRadius: 1,
                backgroundColor: 'white',
                '&:hover': {
                  backgroundColor: '#e3e3e3',
                }
              }}
            >
              <ListItemText primary={text} />
            </ListItem>
          ))}
        </List>
      </Drawer>
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'flex-start', minHeight: 'calc(100vh - 64px)', pt: 3 }}>
        <Box sx={{ width: '80%', maxWidth: 1200 }}>
          {renderContent()}
        </Box>
      </Box>
      <Box sx={{ mt: 4 }}>
        <HealthArticle/>
      </Box>
    </ThemeProvider>
  );
};

export default ClientProfile;
