import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import AppointmentDoctor from './Doctor/Appointments';
import ManageSchedule from './Doctor/ManageSchedule';
import AppointmentStats from './AppointmentStats';
import AppointmentList from './AppointmentList';
import axios from 'axios';
import { 
  Typography, 
  Box, 
  Button, 
  AppBar, 
  Toolbar, 
  IconButton, 
  Drawer, 
  List, 
  ListItem, 
  ListItemText 
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { buildApiUrl } from '../../config/api';

const theme = createTheme();
const drawerWidth = 240;

const DoctorProfile = () => {
  const [appointmentsData, setAppointmentsData] = useState(null);
  const location = useLocation();
  const navigate = useNavigate();

  const [userData, setUserData] = useState(null);
  const [activeSection, setActiveSection] = useState('profile');
  const [drawerOpen, setDrawerOpen] = useState(false);

  useEffect(() => {
    if (location.state?.user) {
      setUserData(location.state.user);
    }
  }, [location.state]);

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        if (!userData || !userData._id) return;

        const response = await axios.get(buildApiUrl(`/appointments/doctor/${userData._id}`));
        const appointments = response.data;
        let pendingCount = 0;
        let confirmedCount = 0;
        let cancelledCount = 0;
        let completedCount = 0;

        appointments.forEach(appointment => {
          switch (appointment.status) {
            case 'Pending':
              pendingCount++;
              break;
            case 'Confirmed':
              confirmedCount++;
              break;
            case 'Cancelled':
              cancelledCount++;
              break;
            case 'Completed':
              completedCount++;
              break;
            default:
              break;
          }
        });

        setAppointmentsData({
          pending: { count: pendingCount, isLoading: false, error: null },
          confirmed: { count: confirmedCount, isLoading: false, error: null },
          cancelled: { count: cancelledCount, isLoading: false, error: null },
          completed: { count: completedCount, isLoading: false, error: null }
        });

      } catch (error) {
        console.error('Error fetching appointments:', error);
      }
    };

    if (userData) {
      fetchAppointments();
    }
  }, [userData]);

  const navigateToSection = (section) => {
    setActiveSection(section);
    setDrawerOpen(false);
  };

  const renderContent = () => {
    if (!userData) {
      return <Typography>Loading...</Typography>;
    }

    switch (activeSection) {
      case 'appointments':
        return <AppointmentDoctor userId={userData._id} />;
      case 'manage-schedule':
        return <ManageSchedule userId={userData._id} />;
      default:
        return (
          <Box sx={{ mt: 4, mb: 2 }}>
            <Typography variant="h4">Doctor Profile</Typography>
            {appointmentsData && (
              <AppointmentStats appointmentsData={appointmentsData} />
            )}
            <AppointmentList doctorId={userData._id}/>
          </Box>
        );
    }
  };

  if (!userData) {
    return <Typography>Loading...</Typography>;
  }

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="static">
          <Toolbar>
            <IconButton
              size="large"
              edge="start"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 2 }}
              onClick={() => setDrawerOpen(true)}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              {userData.fullName} - {userData.email}
              {userData.specializationName && ` - ${userData.specializationName}`}
              {userData.clinicOrHospital && ` - ${userData.clinicOrHospital}`}
            </Typography>
            <Button 
              color="inherit" 
              onClick={() => {
                localStorage.removeItem('token');
                navigate('/login');
              }}
            >
              Logout
            </Button>
          </Toolbar>
        </AppBar>
      </Box>
      <Drawer
        anchor="left"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            background: '#f0f2f5',
            transition: 'background-color 0.3s ease',
          },
          '& .MuiListItem-root': {
            marginTop: '8px',
            marginBottom: '8px',
            background: '#fff',
            transition: 'background-color 0.3s ease',
          },
          '& .MuiListItem-root:hover': {
            background: '#d7dae0',
          },
          '& .MuiListItemText-primary': {
            fontSize: '1.2rem',
            fontWeight: 'bold',
            color: '#333',
            transition: 'color 0.3s ease',
          },
          '& .MuiListItem-root:hover .MuiListItemText-primary': {
            color: '#000',
          },
          '@keyframes fadeIn': {
            from: { opacity: 0 },
            to: { opacity: 1 },
          },
          animation: 'fadeIn 0.5s ease-out',
        }}
      >
        <List>
          {['Profile', 'Appointments', 'Manage Schedule'].map((text, index) => (
            <ListItem 
              button 
              key={text} 
              onClick={() => navigateToSection(text.toLowerCase().replace(' ', '-'))}
              sx={{ background: index % 2 === 0 ? '#f0f2f5' : '#fff' }}
            >
              <ListItemText primary={text} />
            </ListItem>
          ))}
        </List>
      </Drawer>

      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: 'calc(100vh - 64px)' }}>
        <Box sx={{ width: '80%', maxWidth: 1200 }}>
          {renderContent()}
        </Box>
      </Box>
    </ThemeProvider>
  );
};

export default DoctorProfile;
