import React, { useState } from 'react';
import { Box, Typography, Container, Button, Grid, Paper, TextField, Snackbar, Alert, Card, CardContent, CardMedia, Chip, Stack, Divider, Avatar, Rating, MenuItem, Select, FormControl, InputLabel } from '@mui/material';
import { styled } from '@mui/material/styles';
import LocalHospitalIcon from '@mui/icons-material/LocalHospital';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import HealthAndSafetyIcon from '@mui/icons-material/HealthAndSafety';
import MedicalInformationIcon from '@mui/icons-material/MedicalInformation';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import EmailIcon from '@mui/icons-material/Email';
import NewspaperIcon from '@mui/icons-material/Newspaper';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import FormatQuoteIcon from '@mui/icons-material/FormatQuote';
import TipsAndUpdatesIcon from '@mui/icons-material/TipsAndUpdates';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import GroupIcon from '@mui/icons-material/Group';
import VerifiedIcon from '@mui/icons-material/Verified';

// Styled components for animations and modern effects
const HeroSection = styled(Box)(({ theme }) => ({
  position: 'relative',
  backgroundImage: 'linear-gradient(45deg, #03265a 0%, #2752af 100%)',
  color: '#fff',
  overflow: 'hidden',
  paddingTop: theme.spacing(12),
  paddingBottom: theme.spacing(12),
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: 'url("https://img.freepik.com/free-vector/abstract-medical-wallpaper-template-design_53876-61802.jpg") no-repeat center center',
    backgroundSize: 'cover',
    opacity: 0.1,
    zIndex: 0,
  }
}));

const HeroContent = styled(Box)(({ theme }) => ({
  position: 'relative',
  zIndex: 1,
  textAlign: 'center',
}));

const HeroTitle = styled(Typography)(({ theme }) => ({
  fontWeight: 800,
  marginBottom: theme.spacing(2),
  textShadow: '0 2px 10px rgba(0,0,0,0.2)',
  [theme.breakpoints.down('sm')]: {
    fontSize: '2.5rem',
  },
}));

const HeroSubtitle = styled(Typography)(({ theme }) => ({
  marginBottom: theme.spacing(4),
  maxWidth: '800px',
  margin: '0 auto',
  opacity: 0.9,
}));

const AnimatedButton = styled(Button)(({ theme }) => ({
  borderRadius: '50px',
  padding: '12px 30px',
  fontSize: '1rem',
  fontWeight: 600,
  textTransform: 'none',
  boxShadow: '0 4px 20px rgba(255, 255, 255, 0.25)',
  transition: 'all 0.3s ease',
  backgroundColor: '#fff',
  color: '#03265a',
  '&:hover': {
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    transform: 'translateY(-3px)',
    boxShadow: '0 8px 25px rgba(255, 255, 255, 0.35)',
  },
}));

const FeatureCard = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(4),
  height: '100%',
  borderRadius: '16px',
  transition: 'all 0.3s ease',
  boxShadow: '0 8px 30px rgba(0, 0, 0, 0.06)',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  textAlign: 'center',
  '&:hover': {
    transform: 'translateY(-8px)',
    boxShadow: '0 16px 40px rgba(0, 0, 0, 0.09)',
  },
}));

const IconWrapper = styled(Box)(({ theme }) => ({
  background: 'linear-gradient(45deg, #03265a 0%, #2752af 100%)',
  borderRadius: '50%',
  width: '80px',
  height: '80px',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  marginBottom: theme.spacing(2),
  color: '#fff',
  boxShadow: '0 10px 20px rgba(3, 38, 90, 0.2)',
}));

const SectionTitle = styled(Typography)(({ theme }) => ({
  fontWeight: 700,
  marginBottom: theme.spacing(1),
  position: 'relative',
  display: 'inline-block',
  '&::after': {
    content: '""',
    position: 'absolute',
    bottom: '-10px',
    left: '50%',
    transform: 'translateX(-50%)',
    height: '4px',
    width: '60px',
    background: 'linear-gradient(45deg, #03265a 0%, #2752af 100%)',
    borderRadius: '4px',
  },
}));

const ActionButton = styled(Button)(({ theme }) => ({
  borderRadius: '12px',
  padding: '10px 20px',
  textTransform: 'none',
  fontWeight: 600,
  boxShadow: '0 4px 15px rgba(3, 38, 90, 0.15)',
  marginTop: theme.spacing(2),
  backgroundColor: '#03265a',
  '&:hover': {
    backgroundColor: '#2752af',
  },
}));

const NewsletterSection = styled(Box)(({ theme }) => ({
  background: 'linear-gradient(135deg, #f0f7ff 0%, #e6f0ff 100%)',
  padding: theme.spacing(8, 0),
  marginTop: theme.spacing(8),
  borderRadius: theme.spacing(2),
}));

const NewsCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  transition: 'transform 0.3s ease-in-out',
  '&:hover': {
    transform: 'translateY(-8px)',
  },
}));

const TestimonialCard = styled(Card)(({ theme }) => ({
  padding: theme.spacing(3),
  height: '100%',
  borderRadius: '16px',
  transition: 'all 0.3s ease',
  '&:hover': {
    transform: 'translateY(-8px)',
    boxShadow: '0 16px 40px rgba(0, 0, 0, 0.09)',
  },
}));

const StatCard = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  textAlign: 'center',
  borderRadius: '16px',
  background: 'linear-gradient(135deg, #03265a 0%, #2752af 100%)',
  color: 'white',
  transition: 'all 0.3s ease',
  '&:hover': {
    transform: 'translateY(-5px)',
  },
}));

const QuickBookingCard = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(4),
  borderRadius: '20px',
  boxShadow: '0 10px 30px rgba(0, 0, 0, 0.1)',
  background: 'white',
}));

const StyledCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  transition: 'transform 0.3s ease-in-out',
  '&:hover': {
    transform: 'translateY(-8px)',
  },
}));

const Home = () => {
  const [email, setEmail] = useState('');
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [snackbarSeverity, setSnackbarSeverity] = useState('success');
  const [department, setDepartment] = useState('');
  const [doctor, setDoctor] = useState('');
  const [date, setDate] = useState('');

  const handleNewsletterSubmit = (e) => {
    e.preventDefault();
    if (!email) {
      setSnackbarMessage('Please enter your email address');
      setSnackbarSeverity('error');
    } else {
      setSnackbarMessage('Thank you for subscribing to our newsletter!');
      setSnackbarSeverity('success');
      setEmail('');
    }
    setOpenSnackbar(true);
  };

  const healthNews = [
    {
      title: "New Breakthrough in Telemedicine Technology",
      image: "https://img.freepik.com/free-photo/young-woman-having-video-call-with-doctor_23-2148973215.jpg",
      category: "Technology",
      description: "Revolutionary advances in telemedicine are making healthcare more accessible than ever before.",
      date: "March 15, 2024"
    },
    {
      title: "Understanding Mental Health in the Digital Age",
      image: "https://img.freepik.com/free-photo/side-view-woman-having-video-call-with-therapist_23-2148973192.jpg",
      category: "Mental Health",
      description: "Expert insights on maintaining mental wellness in our increasingly connected world.",
      date: "March 14, 2024"
    },
    {
      title: "Latest Updates in Preventive Healthcare",
      image: "https://img.freepik.com/free-photo/doctor-examining-patient-clinic_23-2148973496.jpg",
      category: "Wellness",
      description: "New guidelines for preventive care that everyone should know about.",
      date: "March 13, 2024"
    }
  ];

  const testimonials = [
    {
      name: "Sarah Johnson",
      role: "Patient",
      image: "https://img.freepik.com/free-photo/young-beautiful-woman-pink-warm-sweater-natural-look-smiling-portrait-isolated-long-hair_285396-896.jpg",
      comment: "Health Hub transformed my healthcare experience. The ease of booking appointments and accessing my medical records is incredible.",
      rating: 5
    },
    {
      name: "Dr. Michael Chen",
      role: "Cardiologist",
      image: "https://img.freepik.com/free-photo/doctor-with-his-arms-crossed-white-background_1368-5790.jpg",
      comment: "As a healthcare provider, I appreciate how Health Hub streamlines patient care and communication.",
      rating: 5
    },
    {
      name: "Emily Rodriguez",
      role: "Patient",
      image: "https://img.freepik.com/free-photo/young-woman-with-round-glasses-yellow-sweater_273609-7091.jpg",
      comment: "The telemedicine features have made it so convenient to get medical advice from home.",
      rating: 5
    }
  ];

  const healthTips = [
    {
      title: "Stay Hydrated",
      description: "Drink at least 8 glasses of water daily for optimal health.",
      icon: <TipsAndUpdatesIcon sx={{ fontSize: 40 }} />
    },
    {
      title: "Regular Exercise",
      description: "Aim for 30 minutes of moderate exercise 5 times a week.",
      icon: <TipsAndUpdatesIcon sx={{ fontSize: 40 }} />
    },
    {
      title: "Healthy Sleep",
      description: "Get 7-9 hours of quality sleep each night for better health.",
      icon: <TipsAndUpdatesIcon sx={{ fontSize: 40 }} />
    }
  ];

  const stats = [
    { number: "50,000+", label: "Patients Served", icon: <GroupIcon /> },
    { number: "500+", label: "Qualified Doctors", icon: <LocalHospitalIcon /> },
    { number: "98%", label: "Patient Satisfaction", icon: <VerifiedIcon /> },
    { number: "24/7", label: "Available Support", icon: <AccessTimeIcon /> }
  ];

  return (
    <Box>
      {/* Debug Box */}
      <Alert severity="info" sx={{ position: 'fixed', top: 0, left: 0, right: 0, zIndex: 9999 }}>
        Debug: Home component updated at {new Date().toLocaleString()}
      </Alert>

      <HeroSection>
        <Container maxWidth="lg">
          <HeroContent>
            <HeroTitle variant="h2" component="h1">
              Welcome to Health Hub
            </HeroTitle>
            <HeroSubtitle variant="h5">
              Your comprehensive healthcare solution connecting patients with top medical specialists.
              Book appointments with ease and manage your health journey seamlessly.
            </HeroSubtitle>
            <AnimatedButton variant="contained" size="large" endIcon={<ArrowForwardIcon />}>
              Explore Our Services
            </AnimatedButton>
          </HeroContent>
        </Container>
      </HeroSection>

      <Container maxWidth="lg" sx={{ py: 8 }}>
        <Box sx={{ textAlign: 'center', mb: 6 }}>
          <SectionTitle variant="h3" component="h2">
            Why Choose Health Hub?
          </SectionTitle>
          <Typography variant="body1" color="text.secondary" sx={{ maxWidth: '700px', mx: 'auto', mt: 4 }}>
            We provide comprehensive healthcare solutions to connect you with the best specialists
            and streamline your medical journey with innovative technology.
          </Typography>
        </Box>

        <Grid container spacing={4}>
          <Grid item xs={12} sm={6} md={3}>
            <FeatureCard elevation={2}>
              <IconWrapper>
                <LocalHospitalIcon fontSize="large" />
              </IconWrapper>
              <Typography variant="h6" component="h3" sx={{ fontWeight: 700, my: 2 }}>
                Top Specialists
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Access to qualified medical professionals across multiple specialties and disciplines.
              </Typography>
              <ActionButton variant="contained" size="small" endIcon={<ArrowForwardIcon />}>
                Find Doctors
              </ActionButton>
            </FeatureCard>
          </Grid>

          <Grid item xs={12} sm={6} md={3}>
            <FeatureCard elevation={2}>
              <IconWrapper>
                <CalendarMonthIcon fontSize="large" />
              </IconWrapper>
              <Typography variant="h6" component="h3" sx={{ fontWeight: 700, my: 2 }}>
                Easy Scheduling
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Book appointments online anytime, with instant confirmations and reminders.
              </Typography>
              <ActionButton variant="contained" size="small" endIcon={<ArrowForwardIcon />}>
                Book Now
              </ActionButton>
            </FeatureCard>
          </Grid>

          <Grid item xs={12} sm={6} md={3}>
            <FeatureCard elevation={2}>
              <IconWrapper>
                <HealthAndSafetyIcon fontSize="large" />
              </IconWrapper>
              <Typography variant="h6" component="h3" sx={{ fontWeight: 700, my: 2 }}>
                Patient Care
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Personalized healthcare plans and continuous support throughout your medical journey.
              </Typography>
              <ActionButton variant="contained" size="small" endIcon={<ArrowForwardIcon />}>
                Learn More
              </ActionButton>
            </FeatureCard>
          </Grid>

          <Grid item xs={12} sm={6} md={3}>
            <FeatureCard elevation={2}>
              <IconWrapper>
                <MedicalInformationIcon fontSize="large" />
              </IconWrapper>
              <Typography variant="h6" component="h3" sx={{ fontWeight: 700, my: 2 }}>
                Digital Records
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Secure access to your medical history, test results, and treatment plans all in one place.
              </Typography>
              <ActionButton variant="contained" size="small" endIcon={<ArrowForwardIcon />}>
                View Demo
              </ActionButton>
            </FeatureCard>
          </Grid>
        </Grid>
      </Container>

      <NewsletterSection>
        <Container maxWidth="lg">
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12} md={6}>
              <Typography variant="h3" component="h2" gutterBottom fontWeight="bold">
                Stay Updated with Health Hub
              </Typography>
              <Typography variant="h6" color="text.secondary" paragraph>
                Subscribe to our newsletter for the latest health insights, medical breakthroughs, and wellness tips.
              </Typography>
              <Box component="form" onSubmit={handleNewsletterSubmit} sx={{ mt: 3 }}>
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={8}>
                    <TextField
                      fullWidth
                      label="Your Email Address"
                      variant="outlined"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      sx={{
                        backgroundColor: 'white',
                        '& .MuiOutlinedInput-root': {
                          borderRadius: '50px',
                        }
                      }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={4}>
                    <Button
                      type="submit"
                      fullWidth
                      variant="contained"
                      size="large"
                      startIcon={<EmailIcon />}
                      sx={{
                        borderRadius: '50px',
                        height: '56px',
                        backgroundColor: '#03265a',
                        '&:hover': {
                          backgroundColor: '#2752af',
                        }
                      }}
                    >
                      Subscribe
                    </Button>
                  </Grid>
                </Grid>
              </Box>
            </Grid>
            <Grid item xs={12} md={6}>
              <Box
                component="img"
                src="https://img.freepik.com/free-vector/newsletter-concept-illustration_114360-1033.jpg"
                alt="Newsletter illustration"
                sx={{
                  width: '100%',
                  maxWidth: '500px',
                  display: 'block',
                  margin: '0 auto',
                }}
              />
            </Grid>
          </Grid>
        </Container>
      </NewsletterSection>

      <Container maxWidth="lg" sx={{ py: 8 }}>
        <Box sx={{ textAlign: 'center', mb: 6 }}>
          <SectionTitle variant="h3" component="h2">
            Latest Health News
          </SectionTitle>
          <Typography variant="body1" color="text.secondary" sx={{ maxWidth: '700px', mx: 'auto', mt: 4 }}>
            Stay informed with the latest developments in healthcare and wellness.
          </Typography>
        </Box>

        <Grid container spacing={4}>
          {healthNews.map((news, index) => (
            <Grid item xs={12} md={4} key={index}>
              <StyledCard>
                <CardMedia
                  component="img"
                  height="200"
                  image={news.image}
                  alt={news.title}
                />
                <CardContent>
                  <Chip
                    label={news.category}
                    size="small"
                    color="primary"
                    sx={{ mb: 2 }}
                  />
                  <Typography variant="h6" gutterBottom fontWeight="bold">
                    {news.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>
                    {news.description}
                  </Typography>
                  <Typography variant="caption" display="block" sx={{ mt: 2 }}>
                    {news.date}
                  </Typography>
                </CardContent>
              </StyledCard>
            </Grid>
          ))}
        </Grid>
      </Container>

      <Box sx={{ 
        backgroundColor: 'rgba(3, 38, 90, 0.03)', 
        py: 8 
      }}>
        <Container maxWidth="lg">
          <Grid container spacing={6} alignItems="center">
            <Grid item xs={12} md={6}>
              <Box sx={{ 
                position: 'relative', 
                borderRadius: '20px', 
                overflow: 'hidden',
                boxShadow: '0 20px 40px rgba(0, 0, 0, 0.1)',
              }}>
                <Box 
                  component="img"
                  src="https://img.freepik.com/free-photo/medium-shot-doctor-with-stethoscope_23-2149191355.jpg?t=st=1714935542~exp=1714939142~hmac=af15fb5d1f7c5d7c89dc428d4b680a83a4f5ec1fce5cd649ce69e7ed4c686fea&w=740"
                  alt="Doctor with patient"
                  sx={{ 
                    width: '100%', 
                    height: 'auto', 
                    display: 'block',
                  }}
                />
              </Box>
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography variant="h3" component="h2" sx={{ fontWeight: 700, mb: 3 }}>
                Focused on Patient Experience
              </Typography>
              <Typography variant="body1" sx={{ mb: 3, color: 'text.secondary' }}>
                At Health Hub, we believe healthcare should be accessible, convenient, and personalized. 
                Our platform connects you with the right specialists for your specific needs and simplifies 
                the entire healthcare journey.
              </Typography>
              <Typography variant="body1" sx={{ mb: 4, color: 'text.secondary' }}>
                With intuitive appointment booking, secure messaging with doctors, and easy access to 
                your medical records, we're transforming how you experience healthcare.
              </Typography>
              <Button 
                variant="contained" 
                size="large" 
                sx={{ 
                  borderRadius: '50px',
                  background: 'linear-gradient(45deg, #03265a 0%, #2752af 100%)',
                  textTransform: 'none',
                  fontWeight: 600,
                  padding: '12px 30px',
                }}
              >
                Get Started Today
              </Button>
            </Grid>
          </Grid>
        </Container>
      </Box>

      <Container maxWidth="lg" sx={{ py: 8 }}>
        <Grid container spacing={4}>
          <Grid item xs={12} md={6}>
            <Typography variant="h3" component="h2" sx={{ fontWeight: 700, mb: 3 }}>
              Book Your Appointment
            </Typography>
            <Typography variant="body1" sx={{ mb: 4, color: 'text.secondary' }}>
              Schedule your visit in just a few clicks. Choose your preferred department,
              doctor, and time slot.
            </Typography>
            <QuickBookingCard>
              <Stack spacing={3}>
                <FormControl fullWidth>
                  <InputLabel>Select Department</InputLabel>
                  <Select
                    value={department}
                    onChange={(e) => setDepartment(e.target.value)}
                    label="Select Department"
                  >
                    <MenuItem value="cardiology">Cardiology</MenuItem>
                    <MenuItem value="neurology">Neurology</MenuItem>
                    <MenuItem value="pediatrics">Pediatrics</MenuItem>
                    <MenuItem value="orthopedics">Orthopedics</MenuItem>
                  </Select>
                </FormControl>
                <FormControl fullWidth>
                  <InputLabel>Select Doctor</InputLabel>
                  <Select
                    value={doctor}
                    onChange={(e) => setDoctor(e.target.value)}
                    label="Select Doctor"
                  >
                    <MenuItem value="dr-smith">Dr. Smith</MenuItem>
                    <MenuItem value="dr-johnson">Dr. Johnson</MenuItem>
                    <MenuItem value="dr-williams">Dr. Williams</MenuItem>
                  </Select>
                </FormControl>
                <TextField
                  fullWidth
                  type="date"
                  label="Preferred Date"
                  value={date}
                  onChange={(e) => setDate(e.target.value)}
                  InputLabelProps={{ shrink: true }}
                />
                <Button
                  variant="contained"
                  size="large"
                  sx={{
                    borderRadius: '50px',
                    background: 'linear-gradient(45deg, #03265a 0%, #2752af 100%)',
                    py: 1.5
                  }}
                >
                  Book Appointment
                </Button>
              </Stack>
            </QuickBookingCard>
          </Grid>
          <Grid item xs={12} md={6} sx={{ display: 'flex', alignItems: 'center' }}>
            <Box
              component="img"
              src="https://img.freepik.com/free-vector/hospital-reception-illustration_1284-5415.jpg"
              alt="Appointment booking illustration"
              sx={{ width: '100%', maxWidth: '500px', margin: '0 auto' }}
            />
          </Grid>
        </Grid>
      </Container>

      <Box sx={{ bgcolor: 'rgba(3, 38, 90, 0.02)', py: 8 }}>
        <Container maxWidth="lg">
          <Grid container spacing={4}>
            {stats.map((stat, index) => (
              <Grid item xs={12} sm={6} md={3} key={index}>
                <StatCard>
                  <Box sx={{ mb: 2 }}>
                    {stat.icon}
                  </Box>
                  <Typography variant="h4" component="div" sx={{ fontWeight: 700, mb: 1 }}>
                    {stat.number}
                  </Typography>
                  <Typography variant="body1">
                    {stat.label}
                  </Typography>
                </StatCard>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      <Container maxWidth="lg" sx={{ py: 8 }}>
        <Box sx={{ textAlign: 'center', mb: 6 }}>
          <SectionTitle variant="h3" component="h2">
            Daily Health Tips
          </SectionTitle>
          <Typography variant="body1" color="text.secondary" sx={{ maxWidth: '700px', mx: 'auto', mt: 4 }}>
            Simple yet effective tips to maintain your health and wellness.
          </Typography>
        </Box>
        <Grid container spacing={4}>
          {healthTips.map((tip, index) => (
            <Grid item xs={12} md={4} key={index}>
              <StyledCard>
                <CardContent>
                  <Box sx={{ 
                    display: 'flex', 
                    justifyContent: 'center', 
                    mb: 2,
                    color: '#03265a'
                  }}>
                    {tip.icon}
                  </Box>
                  <Typography variant="h6" component="h3" align="center" gutterBottom>
                    {tip.title}
                  </Typography>
                  <Typography variant="body1" color="text.secondary" align="center">
                    {tip.description}
                  </Typography>
                </CardContent>
              </StyledCard>
            </Grid>
          ))}
        </Grid>
      </Container>

      <Box sx={{ bgcolor: 'rgba(3, 38, 90, 0.02)', py: 8 }}>
        <Container maxWidth="lg">
          <Box sx={{ textAlign: 'center', mb: 6 }}>
            <SectionTitle variant="h3" component="h2">
              What Our Users Say
            </SectionTitle>
            <Typography variant="body1" color="text.secondary" sx={{ maxWidth: '700px', mx: 'auto', mt: 4 }}>
              Read what patients and healthcare providers think about Health Hub.
            </Typography>
          </Box>
          <Grid container spacing={4}>
            {testimonials.map((testimonial, index) => (
              <Grid item xs={12} md={4} key={index}>
                <TestimonialCard>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                    <Avatar
                      src={testimonial.image}
                      sx={{ width: 60, height: 60, mr: 2 }}
                    />
                    <Box>
                      <Typography variant="h6" component="div">
                        {testimonial.name}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {testimonial.role}
                      </Typography>
                    </Box>
                  </Box>
                  <FormatQuoteIcon sx={{ color: '#03265a', fontSize: 40, mb: 2 }} />
                  <Typography variant="body1" paragraph>
                    {testimonial.comment}
                  </Typography>
                  <Rating value={testimonial.rating} readOnly />
                </TestimonialCard>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      <Box component="footer" sx={{ 
        backgroundColor: '#03265a', 
        color: '#fff', 
        py: 5,
        textAlign: 'center'
      }}>
        <Container maxWidth="lg">
          <Typography variant="h6" component="div" sx={{ fontWeight: 700, mb: 2 }}>
            Health Hub
          </Typography>
          <Typography variant="body2" sx={{ opacity: 0.8 }}>
            Connecting patients with healthcare professionals for better health outcomes.
          </Typography>
          <Typography variant="body2" sx={{ mt: 2, opacity: 0.7 }}>
             {new Date().getFullYear()} Health Hub. All rights reserved.
          </Typography>
        </Container>
      </Box>

      <Snackbar
        open={openSnackbar}
        autoHideDuration={6000}
        onClose={() => setOpenSnackbar(false)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert
          onClose={() => setOpenSnackbar(false)}
          severity={snackbarSeverity}
          sx={{ width: '100%' }}
        >
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default Home;
